import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/auth";
import axios from "axios";

const ShowResult = (props) => {
  const [auth, setauth] = useAuth();
  const [UserReport, setUserReport] = useState({});
  console.log(auth.user);
  const navigate = useNavigate();

  function capitalizeFirstLetter(name) {
    return name.replace(/\b\w/g, function (char) {
      return char.toUpperCase();
    });
  }
  const username = capitalizeFirstLetter(auth.user.name);
  useEffect(() => {
    const getUserScore = async () => {
      try {
        const { data } = await axios.post(
          "https://assessment.inboundacademy.in/api/user/user-score",
          {
            userId: auth.user._id,
          },
          {
            headers: {
              Authorization: auth.token,
            },
          }
        );
        if (data) {
          setUserReport(data);
        }
      } catch (error) {
        console.log(error.message);
      }
    };
    getUserScore();
  }, []);
  return (
    <div class="content_wraper">
      <div class="logo">
        <div class="container">
          <a target="_blank" href="https://www.inboundacademy.in/">
            <img src="images/inbound-academy-logo.webp" alt="Logo" />
          </a>
        </div>
      </div>
      <div class="body_content">
        <div class="container">
          <div class="exam_start">
            <div class="exam_result_gh">
              <div class="exam_name">Inbound Academy Assessement Test</div>
              <div class="exam_score_details">
                <div class="candidate_dt">
                  <span>
                    Candidate Name:{" "}
                    <strong>{capitalizeFirstLetter(username)}</strong>
                  </span>

                  <span>
                    Number of Questions Attempted:{" "}
                    <strong>{UserReport.total_attempted}</strong>
                  </span>
                </div>
                <div class="result_box">
                  <span>Total Score:</span>
                  <div class="reult_score">
                    {UserReport.marks_Obtained}/{UserReport.max_marks}
                  </div>
                  {UserReport.marks_Obtained < 7 ? (
                    <span className="gd_bag">Failed</span>
                  ) : (
                    <span class="gd_bad">PASSED</span>
                  )}
                </div>
              </div>
            </div>
            <div class="exam_start_cta text-center">
              {auth?.score >= 30 ? (
                <Link to={"payment"}>Proceed Payment</Link>
              ) : (
                ""
              )}
            </div>
          </div>
        </div>
      </div>
      <footer class="text-center">
        <div class="container">
          Copyright Â© 2023.{" "}
          <a href="https://www.transfunnel.com/" target="_blank">
            TransFunnel Consulting
          </a>
        </div>
      </footer>
    </div>
  );
};

export default ShowResult;
