import React, { useEffect, useState } from "react";

import ShowResult from "./ShowResult";
import axios from "axios";
function Question() {
  const [Qestion, setQuestion] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [clickedOption, setClickedOption] = useState(0);
  const [showResult, setShowResult] = useState(false);

  const changeQuestion = () => {
    updateScore();
    if (currentQuestion < Question.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setClickedOption(0);
    } else {
      setShowResult(true);
    }
  };
  const updateScore = () => {
    if (clickedOption === Question[currentQuestion].answer) {
      setScore(score + 1);
    }
  };
  // const resetAll=()=>{
  //     setShowResult(false);
  //     setCurrentQuestion(0);
  //     setClickedOption(0);
  //     setScore(0);
  // }

  //get all question from database

  const getQuestion = async () => {
    try {
      const { data } = await axios.get(
        "http://localhost:5000/api/user/question/1"
      );
      console.log(data);
      if (data) {
        setQuestion(data.questions);
      }
    } catch (error) {
      console.log(error.message);
    }
  };
  //lifecycle hooks
  useEffect(() => {
    getQuestion();
  }, []);
  return (
    <div>
      <p className="heading-txt">Inboud Test</p>
      <div className="container">
        {showResult ? (
          <ShowResult score={score} totalScore={Question.length} />
        ) : (
          <>
            <div className="question">
              <span id="question-number">{currentQuestion + 1}. </span>
              <span id="question-txt">
                {Question[currentQuestion].question}
              </span>
            </div>
            <div className="option-container">
              {Question[currentQuestion].options.map((option, i) => {
                return (
                  <button
                    // className="option-btn"
                    className={`option-btn ${
                      clickedOption == i + 1 ? "checked" : null
                    }`}
                    key={i}
                    onClick={() => setClickedOption(i + 1)}
                  >
                    {option}
                  </button>
                );
              })}
            </div>
            <input
              type="button"
              value="Next"
              id="next-button"
              onClick={changeQuestion}
            />
          </>
        )}
      </div>
    </div>
  );
}

export default Question;
