import React, { useEffect, useState } from "react";

import ShowResult from "./ShowResult";
import axios from "axios";
import { Link } from "react-router-dom";
import { useAuth } from "../context/auth";
function Question() {
  const [Question, setQuestion] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);

  const [Answer, setAnswer] = useState();
  const [Timer, seTimer] = useState("");
  const [auth, setAuth] = useAuth();

  const onChangeHandeler = (e) => {
    setAnswer(e.target.value);
    console.log(e.target.value);
  };
  const calculateAnswer = () => {
    setCurrentQuestion(currentQuestion + 1);
    if (Question?.[currentQuestion].answer === Answer) {
      setScore(score + 1);
    } else {
      setScore(score + 0);
    }
    console.log(Question?.[currentQuestion].answer);
    setAuth({ ...auth, score: score });
  };

  function startTimer(duration) {
    let timer = duration;
    // const timerElement = document.getElementById("timer");

    const interval = setInterval(function () {
      const hours = Math.floor(timer / 3600);
      const minutes = Math.floor((timer % 3600) / 60);
      const seconds = timer % 60;

      const formattedTime =
        (hours < 10 ? "0" : "") +
        hours +
        ":" +
        (minutes < 10 ? "0" : "") +
        minutes +
        ":" +
        (seconds < 10 ? "0" : "") +
        seconds;

      seTimer(formattedTime);

      if (--timer < 0) {
        clearInterval(interval);
        seTimer("00:00:00");
      }
    }, 1000);
  }
  //get all question from database

  //lifecycle hooks
  useEffect(() => {
    const getQuestion = async () => {
      try {
        const { data } = await axios.get(
          "http://localhost:5000/api/user/question/1"
        );
        console.log(data.questions);
        if (data) {
          setQuestion(data.questions);
          // console.log(Question);
        }
      } catch (error) {
        console.log(error.message);
      }
    };
    getQuestion();
  }, []);

  useEffect(() => {
    startTimer(3600);
  }, []);

  //video recording function

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorder.stop();
    } catch (error) {}
  };

  return (
    <div class="content_wraper">
      <div class="logo">
        <div class="container">
          <a target="_blank" href="https://www.inboundacademy.in/">
            <img src="images/inbound-academy-logo.webp" alt="Logo" />
          </a>
        </div>
      </div>
      <div class="body_content">
        <div class="container">
          <div class="exam_wrap">
            <div class="exam_title">
              <h1>Salesforce Administrator Certification Practice Test</h1>
            </div>
            <div class="exam_time">
              Time Remaining: &nbsp;{Timer}
              <strong>
                <span id="timer"></span>
              </strong>
            </div>
            <div class="exam_qustions">
              <div class="qus_nums">{`${currentQuestion + 1} of ${
                Question.length
              }`}</div>
              <div class="qus_ans">
                <div class="qustion">
                  {Question?.[currentQuestion]?.question}
                </div>

                <div class="qustion_mcqs">
                  <ul type="A">
                    {Question?.[currentQuestion]?.option?.map(
                      (option, index) => (
                        <li>
                          <span>
                            <input
                              type="radio"
                              name="ans"
                              value={index}
                              id={
                                Question?.[currentQuestion]?._id + `_${index}`
                              }
                              onClick={onChangeHandeler}
                            />
                            <label
                              htmlFor={
                                Question?.[currentQuestion]?._id + `_${index}`
                              }
                            >
                              {option}
                            </label>
                          </span>
                        </li>
                      )
                    )}
                  </ul>
                </div>
              </div>
            </div>
            <div class="exam_qustions_btns text-right">
              {currentQuestion >= 1 ? (
                <button
                  onClick={() => setCurrentQuestion(currentQuestion - 1)}
                  class=" btn"
                >
                  <i class="fa-solid fa-angles-left"></i>&nbsp;Back
                </button>
              ) : (
                ""
              )}

              {currentQuestion + 1 === Question.length ? (
                <Link className="btn" to={"/result"}>
                  Submit Exam
                </Link>
              ) : (
                <button onClick={() => calculateAnswer()} class="btn">
                  Next&nbsp;<i class="fa-solid fa-angles-right"></i>
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
      <footer class="text-center">
        <div class="container">
          Copyright Â© 2023.{" "}
          <a href="https://www.transfunnel.com/" target="_blank">
            TransFunnel Consulting
          </a>
        </div>
      </footer>
    </div>
  );
}

export default Question;
