import React, { useEffect, useState } from "react";

import ShowResult from "./ShowResult";
import axios from "axios";
function Question() {
  const [Question, setQuestion] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [clickedOption, setClickedOption] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [Answer, setAnswer] = useState();
  const [Timer, seTimer] = useState("");

  const changeQuestion = () => {
    updateScore();
    if (currentQuestion < Question.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setClickedOption(0);
    } else {
      setShowResult(true);
    }
  };
  const updateScore = () => {
    if (clickedOption === Question[currentQuestion].answer) {
      setScore(score + 1);
    }
  };
  // const resetAll=()=>{
  //     setShowResult(false);
  //     setCurrentQuestion(0);
  //     setClickedOption(0);
  //     setScore(0);
  // }

  //get all question from database

  //lifecycle hooks
  useEffect(() => {
    const getQuestion = async () => {
      try {
        const { data } = await axios.get(
          "http://localhost:5000/api/user/question/1"
        );
        console.log(data.questions);
        if (data) {
          setQuestion(data.questions);
          // console.log(Question);
        }
      } catch (error) {
        console.log(error.message);
      }
    };
    getQuestion();
  }, []);
  const onChangeHandeler = (e) => {
    setAnswer(e.target.value);
  };
  const calculateAnswer = () => {
    setCurrentQuestion(currentQuestion + 1);
    if (Question?.[currentQuestion].answer === Answer) {
      setScore(score + 1);
    }
  };

  function startTimer(duration) {
    let timer = duration;
    // const timerElement = document.getElementById("timer");

    const interval = setInterval(function () {
      const hours = Math.floor(timer / 3600);
      const minutes = Math.floor((timer % 3600) / 60);
      const seconds = timer % 60;

      const formattedTime =
        (hours < 10 ? "0" : "") +
        hours +
        ":" +
        (minutes < 10 ? "0" : "") +
        minutes +
        ":" +
        (seconds < 10 ? "0" : "") +
        seconds;
      console.log(formattedTime);
      seTimer(formattedTime);

      if (--timer < 0) {
        clearInterval(interval);
        seTimer("00:00:00");
      }
    }, 1000);
  }
  startTimer(3600);
  return (
    // <></>
    // <div>
    //   <p className="heading-txt">Inboud Test</p>
    //   <div className="container">
    //     {showResult ? (
    //       <ShowResult score={score} totalScore={Question.length} />
    //     ) : (
    //       <>
    //         <div className="question">
    //           <span id="question-number">{currentQuestion + 1}. </span>
    //           {/* <span id="question-txt">{Question[currentQuestion].option}</span> */}
    //           {console.log()}
    //         </div>
    //         <div className="option-container">
    //           {Question[currentQuestion].option.map((option, i) => {
    //             return (
    //               <button
    //                 // className="option-btn"
    //                 className={`option-btn ${
    //                   clickedOption == i + 1 ? "checked" : null
    //                 }`}
    //                 key={i}
    //                 onClick={() => setClickedOption(i + 1)}
    //               >
    //                 {option}
    //               </button>
    //             );
    //           })}
    //         </div>
    //         <input
    //           type="button"
    //           value="Next"
    //           id="next-button"
    //           onClick={changeQuestion}
    //         />
    //       </>
    //     )}
    //   </div>
    // </div>
    <div class="content_wraper">
      <div class="logo">
        <div class="container">
          <a target="_blank" href="https://www.inboundacademy.in/">
            <img src="images/inbound-academy-logo.webp" alt="Logo" />
          </a>
        </div>
      </div>
      <div class="body_content">
        <div class="container">
          <div class="exam_wrap">
            <div class="exam_title">
              <h1>Salesforce Administrator Certification Practice Test</h1>
            </div>
            <div class="exam_time">
              Time Remaining: &nbsp;{Timer}
              <strong>
                <span id="timer"></span>
              </strong>
            </div>
            <div class="exam_qustions">
              <div class="qus_nums">{`${currentQuestion + 1} of ${
                Question.length
              }`}</div>
              <div class="qus_ans">
                <div class="qustion">
                  {Question?.[currentQuestion]?.question}
                </div>

                <div class="qustion_mcqs">
                  <ul type="A">
                    {Question?.[currentQuestion]?.option?.map(
                      (option, index) => (
                        <li key={index}>
                          <span>
                            <input
                              type="radio"
                              name="ans"
                              value={index}
                              id={
                                Question?.[currentQuestion]?._id + `_${index}`
                              }
                              onClick={onChangeHandeler}
                            />
                            <label
                              htmlFor={
                                Question?.[currentQuestion]?._id + `_${index}`
                              }
                            >
                              {option}
                            </label>
                          </span>
                        </li>
                      )
                    )}
                  </ul>
                </div>
              </div>
            </div>
            <div class="exam_qustions_btns text-right">
              {currentQuestion >= 1 ? (
                <button
                  onClick={() => setCurrentQuestion(currentQuestion - 1)}
                  class=" btn"
                >
                  <i class="fa-solid fa-angles-left"></i>&nbsp;Back
                </button>
              ) : (
                ""
              )}

              {currentQuestion + 1 === Question.length ? (
                <button class="btn">Submit Exam</button>
              ) : (
                <button onClick={() => calculateAnswer} class="btn">
                  Next&nbsp;<i class="fa-solid fa-angles-right"></i>
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
      <footer class="text-center">
        <div class="container">
          Copyright Â© 2023.{" "}
          <a href="https://www.transfunnel.com/" target="_blank">
            TransFunnel Consulting
          </a>
        </div>
      </footer>
    </div>
  );
}

export default Question;
