import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { useAuth } from "../context/auth";
import axios from "axios";

const Login = ({}) => {
  const [auth, setauth] = useState({});
  const [Otp, setOtp] = useState("");
  const [Valid, setValid] = useState(false);

  const navigate = useNavigate();

  const search = useLocation().search;
  const id = new URLSearchParams(search).get("id");

  //event handler
  const onChangeHandler = (e) => {
    const userOtp = e.target.value;

    if (userOtp) {
      setOtp(userOtp);
    }
  };
  const getUserInfo = async () => {
    try {
      const { data } = await axios.post(
        "https://assessment.inboundacademy.in/api/user/login",
        {
          id: id,
          otp: Otp,
        }
      );
      if (data) {
        setauth({ ...auth, user: data.userInfo, token: data.token });
        localStorage.setItem("auth", JSON.stringify(data));
        setValid(true);
        navigate("/home");
      } else {
        window.alert("something went wrong");
      }
    } catch (error) {
      window.alert("invalid OTP");
    }
  };

  //validation
  const userValidation = async () => {
    await getUserInfo();
    if (Valid) {
      navigate("/home");
    } else {
      // window.alert("please enter valid otp");
    }
  };

  const resendOtp = async () => {
    try {
      const { data } = await axios.post(
        "https://assessment.inboundacademy.in/api/user/resend-otp",
        {
          id: id,
        }
      );
      if (data) {
        const res = await axios.post(
          "https://transfunnel.io/projects/hs_transfunnel/inbound_submission_api.php",
          {
            full_name: data.data.name,
            phone: data.data.phone,
            email: data.data.email,
          }
        );
        if (res) {
          console.log(res);
        }
      }
    } catch (error) {
      console.log(error.message);
    }
  };
  return (
    <div class="otp_wraper">
      <div class="otp_form">
        <div class="logo_otp text-center">
          <a target="_blank" href="#">
            <img
              width="170"
              src="images/inbound-academy-logo.webp"
              alt="Logo"
            />
          </a>
        </div>
        <div class="otp_content text-center">
          <h3>Verify Mobile</h3>
          <p>Enter the OTP (One Time Password) Sent to +91 91525852585</p>
          <input
            onChange={onChangeHandler}
            value={Otp}
            type="text"
            placeholder="Enter OTP here"
            required
          />

          {/* <input type="submit" value="Verify OTP" /> */}
          <button className="btn" type="submit" onClick={userValidation}>
            VERIFY OTP
          </button>
        </div>
        <div class="otp_resend text-center">
          <p>Check your Phone for the OTP</p>
          <button onClick={resendOtp} className="btn">
            Resend OTP
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
