import React, { useEffect, useState } from "react";
import ReactWebcam from "react-webcam";
import ShowResult from "./ShowResult";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/auth";

function Question() {
  const navigate = useNavigate();
  const [Question, setQuestion] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);

  const [Timer, seTimer] = useState("");
  const [auth, setAuth] = useAuth();
  const [Update, setUpdate] = useState([]);
  const [Clicked, setClicked] = useState(false);

  const [selectedOptions, setSelectedOptions] = useState({});

  const handleOptionSelect = (questionId, optionId, e) => {
    setSelectedOptions((prevSelected) => ({
      ...prevSelected,
      [questionId]: optionId,
    }));
    changeHandleer(e);
  };

  const calculateAnswer = () => {
    if (!Clicked) {
      window.alert("answer is required");
    } else {
      setCurrentQuestion(currentQuestion + 1);
      setClicked(false);
    }
  };

  function startTimer(duration) {
    let timer = duration;

    const interval = setInterval(function () {
      const hours = Math.floor(timer / 3600);
      const minutes = Math.floor((timer % 3600) / 60);
      const seconds = timer % 60;

      const formattedTime =
        (hours < 10 ? "0" : "") +
        hours +
        ":" +
        (minutes < 10 ? "0" : "") +
        minutes +
        ":" +
        (seconds < 10 ? "0" : "") +
        seconds;

      seTimer(formattedTime);

      if (--timer < 0) {
        clearInterval(interval);
        seTimer("00:00:00");
      }
    }, 1000);
  }
  //get all question from database

  //lifecycle hooks
  useEffect(() => {
    const getQuestion = async () => {
      try {
        const { data } = await axios.post(
          "https://assessment.inboundacademy.in/api/user/questions",
          {
            hindi: "3",

            english: "3",

            math: "3",
          },
          {
            headers: {
              Authorization: auth.token,
            },
          }
        );

        if (data) {
          setQuestion(data.data);

          setUpdate(data.data);
        }
      } catch (error) {
        console.log(error.message);
      }
    };
    getQuestion();
    startTimer(3600);
  }, []);

  async function changeHandleer(e) {
    setClicked(true);

    const userAnswer = Number(e.target.value);
    Update.forEach((item) => {
      if (item._id === Question[currentQuestion]._id) {
        item.user_res = userAnswer;
      }
    });
  }
  // onsubmit
  const onSubmitHandler = async () => {
    try {
      // console.log(auth.user);
      const { data } = await axios.post(
        "https://assessment.inboundacademy.in/api/user/answer-response",
        {
          userId: auth.user._id,
          userAnswer: Update,
        }
      );

      if (data) {
        navigate("/result");
        // console.log(data);
      }
    } catch (error) {
      console.log(error.message);
    }
  };
  let hours = 10;

  let now = new Date().getTime();
  var setupTime = localStorage.getItem("setupTime");
  if (setupTime == null) {
    localStorage.setItem("setupTime", now);
  } else {
    if (now - setupTime > hours * 1000) {
      localStorage.removeItem("setupTime");
      onSubmitHandler();
    }
  }

  return (
    <div className="content_wraper">
      <div className="logo">
        <div className="container">
          <a target="_blank" href="https://www.inboundacademy.in/">
            <img src="images/inbound-academy-logo.webp" alt="Logo" />
          </a>
        </div>
      </div>

      <div className="body_content">
        <div className="container">
          <div className="exam_wrap">
            <div className="exam_title">
              <h1>Inbound Academy Assessement Test</h1>
            </div>
            {/* <ReactWebcam height={200} width={200} /> */}

            <div className="exam_time">
              Time Remaining: &nbsp;{Timer}
              <strong>
                <span id="timer"></span>
              </strong>
            </div>
            <div className="exam_qustions">
              <div className="qus_nums">{`${currentQuestion + 1} of ${
                Question.length
              }`}</div>
              <div className="qus_ans">
                <div className="qustion">
                  {Question?.[currentQuestion]?.question}
                </div>

                <div className="qustion_mcqs">
                  <ul type="A">
                    {Question?.[currentQuestion]?.options?.map(
                      (option, index) => (
                        <label key={index}>
                          <input
                            type="radio"
                            name={`question-${Question?.[currentQuestion]?._id}`}
                            value={index + 1}
                            checked={
                              selectedOptions[
                                Question?.[currentQuestion]?._id
                              ] === index
                            }
                            onChange={(e) =>
                              handleOptionSelect(
                                Question?.[currentQuestion]?._id,
                                index,
                                e
                              )
                            }
                          />
                          {option}
                        </label>
                      )
                    )}
                  </ul>
                </div>
              </div>
            </div>
            <div className="exam_qustions_btns text-right">
              {currentQuestion >= 1 ? (
                <button
                  onClick={() => setCurrentQuestion(currentQuestion - 1)}
                  className=" btn"
                >
                  <i className="fa-solid fa-angles-left"></i>&nbsp;Back
                </button>
              ) : (
                ""
              )}

              {currentQuestion + 1 === Question.length ? (
                // <Link className="btn" to={"/result"}>
                //   Submit Exam
                // </Link>
                <button
                  className="btn"
                  type="submit"
                  onClick={() => onSubmitHandler()}
                >
                  Submit Exam
                </button>
              ) : (
                // <></>
                // <></>
                <button onClick={() => calculateAnswer()} className="btn">
                  Next&nbsp;<i className="fa-solid fa-angles-right"></i>
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
      <footer className="text-center">
        <div className="container">
          Copyright © 2023.{" "}
          <a href="https://www.transfunnel.com/" target="_blank">
            TransFunnel Consulting
          </a>
        </div>
      </footer>
    </div>
  );
}

export default Question;
