import React, { useEffect, useState } from "react";
import axios from "axios";

const Question = () => {
  const [Qestion, setQuestion] = useState([]);

  //lifeCycle function
  useEffect(() => {
    getQuestion();
  }, []);
  //get all question from database

  const getQuestion = async () => {
    try {
      const { data } = await axios.get(
        "http://localhost:5000/api/user/question/1"
      );
      console.log(data);
      if (data) {
        setQuestion(data.questions);
      }
    } catch (error) {
      console.log(error.message);
    }
  };
  return (
    <>
      {Qestion.map((item, index) => {
        return (
          <div key={index}>
            <h1>{item.question}</h1>
            {item.option.map((option, index) => (
              // <p key={index}>{option}</p>
              <label htmlFor="">
                <input type="radio" value={index} />
                {option}
              </label>
            ))}
          </div>
        );
      })}
    </>
  );
};

export default Question;
