import React, { useEffect, useState } from "react";
import ReactWebcam from "react-webcam";

import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/auth";
import { toast } from "react-toastify";
import Spinner from "./Spinner";

function Question() {
  const navigate = useNavigate();
  const [Question, setQuestion] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [Timer, seTimer] = useState("");
  const [auth, setAuth] = useAuth();
  const [Update, setUpdate] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState({});
  const [category, setCategory] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const makeUniqueCategory = (arr) => {
    let set = new Set();
    arr.forEach((item) => {
      // console.log(item.category);

      set.add(item.category.replaceAll("_", " "));
    });

    setCategory([...set]);
  };
  const handleOptionSelect = (questionId, optionId, e) => {
    setSelectedOptions((prevSelected) => ({
      ...prevSelected,
      [questionId]: optionId,
    }));
    changeHandleer(e);
  };

  const calculateAnswer = () => {
    const trackResults = Question[currentQuestion];
    Update.forEach((item) => {
      if (item._id === trackResults._id) {
        if (item.user_res === null) {
          toast.error("Answer is required!");
        } else {
          setCurrentQuestion(currentQuestion + 1);
        }
      }
    });
  };

  function startTimer(duration) {
    let timer = duration;

    const interval = setInterval(function () {
      const hours = Math.floor(timer / 3600);
      const minutes = Math.floor((timer % 3600) / 60);
      const seconds = timer % 60;

      const formattedTime =
        (hours < 10 ? "0" : "") +
        hours +
        ":" +
        (minutes < 10 ? "0" : "") +
        minutes +
        ":" +
        (seconds < 10 ? "0" : "") +
        seconds;

      seTimer(formattedTime);

      if (--timer < 0) {
        clearInterval(interval);
        seTimer("00:00:00");
      }
    }, 1000);
  }
  //get all question from database

  //lifecycle hooks
  // function setTimers() {
  //   let hours = 1;

  //   let now = new Date().getTime();
  //   console.log(now);
  //   let setupTime = localStorage.getItem("setupTime");
  //   if (setupTime == null) {
  //     localStorage.setItem("setupTime", now);
  //   } else {
  //     if (now - setupTime >= 60 * 1000) {
  //       onSubmitHandler();
  //       localStorage.removeItem("setupTime");
  //     }
  //   }
  // }
  const getQuestion = async () => {
    try {
      const { data } = await axios.post(
        "https://assessment.inboundacademy.in/api/user/questions",
        {
          Aptitude: "5",

          Communication: "5",

          Logic_Based: "5",
          Maths: "5",
        },

        {
          headers: {
            Authorization: `Bearer ${auth.token}`,
          },
        }
      );

      if (data) {
        setQuestion(data.data);
        makeUniqueCategory(data.data);
        setIsLoading(false);
        setUpdate(data.data);
      }
    } catch (error) {
      toast.error("please contact us!");
      localStorage.removeItem("auth");
      window.location.href = "https://www.inboundacademy.in/";
    }
  };
  const setLocalStorageWithTimestamp = () => {
    const currentTime = new Date().getTime();
    localStorage.setItem(
      "myData",
      JSON.stringify({ value: "yourValue", timestamp: currentTime })
    );
  };
  const checkAndRemoveLocalStorage = () => {
    const storedData = localStorage.getItem("myData");
    if (storedData) {
      const { value, timestamp } = JSON.parse(storedData);
      const currentTime = new Date().getTime();
      const oneHourInMilliseconds = 60 * 1000;

      if (currentTime - timestamp >= oneHourInMilliseconds) {
        // One hour has passed, remove the item from localStorage
        console.log(Update);
        onSubmitHandler();
        localStorage.removeItem("myData");
      }
    }
  };
  useEffect(() => {
    getQuestion();
    startTimer(3600);
    setLocalStorageWithTimestamp();
    const interval = setInterval(() => {
      checkAndRemoveLocalStorage();
    }, 60000);
    // Check every minute

    // Cleanup the interval when the component unmounts
    return () => clearInterval(interval);
  }, []);

  function changeHandleer(e) {
    const userAnswer = Number(e.target.value);
    Update.forEach((item) => {
      if (item._id === Question[currentQuestion]._id) {
        item.user_res = userAnswer;
      }
    });
  }
  // onsubmit
  async function onSubmitHandler() {
    try {
      const { data } = await axios.post(
        "https://assessment.inboundacademy.in/api/user/answer-response",
        {
          userId: auth.user._id,
          userAnswer: Update,
        }
      );

      if (data) {
        navigate("/result");
        localStorage.clear();
      }
    } catch (error) {
      toast.error("something went wrong");
    }
  }

  return (
    <>
      {isLoading ? (
        <Spinner />
      ) : (
        <div className="content_wraper">
          <div className="logo">
            <div className="container">
              <a target="_blank" href="https://assessment.inboundacademy.in">
                <img src="images/inbound-academy-logo.webp" alt="Logo" />
              </a>
            </div>
          </div>

          <div className="body_content">
            <div className="container">
              <div className="exam_wrap">
                <div className="row">
                  <div className="col-md-9">
                    <div className="exam_title">
                      <h1>Inbound Academy Assessement Test</h1>
                    </div>
                    {/* <div className="exam_cats">
               <span>{Question?.[currentQuestion]?.category}</span>
             </div> */}
                    <div className="exam_cats">
                      {category.map((item) => (
                        <span
                          className={
                            (Question?.[currentQuestion]?.category).replaceAll(
                              "_",
                              " "
                            ) == item
                              ? "active_cat"
                              : ""
                          }
                        >
                          {item}
                        </span>
                      ))}
                      {/* <span className="active_cat">


                 {Question?.[currentQuestion]?.category}
               </span>
               <span>PHP</span>
               <span>MYSQL</span> */}
                    </div>
                    <div className="exam_qustions">
                      <div className="qus_nums">
                        <span>{`${currentQuestion + 1} of ${
                          Question.length
                        }`}</span>
                      </div>
                      <div className="qus_ans">
                        <div className="qustion">
                          {Question?.[currentQuestion]?.question}
                        </div>
                        <div className="qustion_mcqs">
                          <ul type="A">
                            {Question?.[currentQuestion]?.options?.map(
                              (option, index) => (
                                <label key={index} className="ans-label">
                                  <input
                                    type="radio"
                                    name={`question-${Question?.[currentQuestion]?._id}`}
                                    value={index + 1}
                                    checked={
                                      selectedOptions[
                                        Question?.[currentQuestion]?._id
                                      ] === index
                                    }
                                    onChange={(e) =>
                                      handleOptionSelect(
                                        Question?.[currentQuestion]?._id,
                                        index,
                                        e
                                      )
                                    }
                                  />
                                  <span></span>
                                  {option}
                                </label>
                              )
                            )}
                          </ul>
                        </div>
                      </div>
                    </div>
                    <div className="exam_qustions_btns text-right">
                      {currentQuestion >= 1 ? (
                        <button
                          onClick={() =>
                            setCurrentQuestion(currentQuestion - 1)
                          }
                          className=" btn"
                        >
                          <i className="fa-solid fa-angles-left"></i>&nbsp;Back
                        </button>
                      ) : (
                        ""
                      )}

                      {currentQuestion + 1 === Question.length ? (
                        <button
                          className="btn"
                          type="submit"
                          onClick={() => onSubmitHandler()}
                        >
                          Submit Exam
                        </button>
