import React, { useEffect, useState } from "react";
import ReactWebcam from "react-webcam";
import ShowResult from "./ShowResult";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/auth";

function Question() {
  const navigate = useNavigate();
  const [Question, setQuestion] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selected, setselected] = useState(null);
  const [Answer, setAnswer] = useState();
  const [Timer, seTimer] = useState("");
  const [auth, setAuth] = useAuth();
  const [Update, setUpdate] = useState([]);
  const [Clicked, setClicked] = useState(false);

  const calculateAnswer = () => {
    // if (!Clicked) {
    //   window.alert("answer is required");
    // } else {
    setCurrentQuestion(currentQuestion + 1);
    setClicked(false);
    // }
  };

  function startTimer(duration) {
    let timer = duration;

    const interval = setInterval(function () {
      const hours = Math.floor(timer / 3600);
      const minutes = Math.floor((timer % 3600) / 60);
      const seconds = timer % 60;

      const formattedTime =
        (hours < 10 ? "0" : "") +
        hours +
        ":" +
        (minutes < 10 ? "0" : "") +
        minutes +
        ":" +
        (seconds < 10 ? "0" : "") +
        seconds;

      seTimer(formattedTime);

      if (--timer < 0) {
        clearInterval(interval);
        seTimer("00:00:00");
      }
    }, 1000);
  }
  //get all question from database

  //lifecycle hooks
  useEffect(() => {
    const getQuestion = async () => {
      try {
        const { data } = await axios.get(
          "http://92.205.18.107:4001/api/user/question/1"
        );

        if (data) {
          setQuestion(data.questions);

          setUpdate(data.questions);
        }
      } catch (error) {
        console.log(error.message);
      }
    };
    getQuestion();
    {
    }
  }, []);

  useEffect(() => {
    startTimer(3600);
  }, []);
  function changeHandleer(e) {
    setClicked(true);
    console.log(e.target.value);
    const userAnswer = Number(e.target.value);
    Update.forEach((item) => {
      if (item._id === Question[currentQuestion]._id) {
        console.log("sanskar");
        item.answer = userAnswer;
      }
    });
  }

  // onsubmit
  const onSubmitHandler = () => {
    let userAnswer = 0;
    Question.map((item) => {
      Update.forEach((userItem) => {
        if (userItem._id === item._id && userItem.answer === item.answer) {
          userAnswer++;
        }
      });
    });

    setAuth({ ...auth, score: userAnswer });
    navigate("/result");
  };
  return (
    <div className="content_wraper">
      <div className="logo">
        <div className="container">
          <a target="_blank" href="https://www.inboundacademy.in/">
            <img src="images/inbound-academy-logo.webp" alt="Logo" />
          </a>
        </div>
      </div>
      <div className="body_content">
        <div className="container">
          <div className="exam_wrap">
            <div className="exam_title">
              <h1>Salesforce Administrator Certification Practice Test</h1>
            </div>
            {/* <ReactWebcam height={200} width={200} /> */}

            <div className="exam_time">
              Time Remaining: &nbsp;{Timer}
              <strong>
                <span id="timer"></span>
              </strong>
            </div>
            <div className="exam_qustions">
              <div className="qus_nums">{`${currentQuestion + 1} of ${
                Question.length
              }`}</div>
              <div className="qus_ans">
                <div className="qustion">{Question?.[0]?.question}</div>

                <div className="qustion_mcqs">
                  <ul type="A">
                    {/* {Question?.[currentQuestion]?.options?.map(
                      (option, index) => (
                        <li key={index}>
                          <span>
                            <input
                              type="radio"
                              name={`ques_${currentQuestion}`}
                              value={index + 1}
                              id={
                                Question?.[currentQuestion]?._id + `_${index}`
                              }
                              onChange={changeHandleer}
                            />
                            <label
                              htmlFor={
                                Question?.[currentQuestion]?._id + `_${index}`
                              }
                            >
                              {option}
                            </label>
                          </span>
                        </li>
                      )
                    )} */}
                    {Question?.[currentQuestion]?.options ? (
                      <>
                        <label>
                          <input
                            type="radio"
                            name={`question-${Question?.[currentQuestion]?.options[0]}`}
                          />
                          {Question?.[currentQuestion]?.options[0]}
                        </label>
                        <label>
                          <input
                            type="radio"
                            name={`question-${Question?.[currentQuestion]?.options[0]}`}
                          />
                          {Question?.[currentQuestion]?.options[1]}
                        </label>
                        <label>
                          <input
                            type="radio"
                            name={`question-${Question?.[currentQuestion]?.options[0]}`}
                          />
                          {Question?.[currentQuestion]?.options[2]}
                        </label>
                        <label>
                          <input
                            type="radio"
                            name={`question-${Question?.[currentQuestion]?.options[0]}`}
                          />
                          {Question?.[currentQuestion]?.options[3]}
                        </label>
                      </>
                    ) : (
                      ""
                    )}
                  </ul>
                </div>
              </div>
            </div>
            <div className="exam_qustions_btns text-right">
              {currentQuestion >= 1 ? (
                <button
                  onClick={() => setCurrentQuestion(currentQuestion - 1)}
                  className=" btn"
                >
                  <i className="fa-solid fa-angles-left"></i>&nbsp;Back
                </button>
              ) : (
                ""
              )}

              {currentQuestion + 1 === Question.length ? (
                // <Link className="btn" to={"/result"}>
                //   Submit Exam
                // </Link>
                <button className="btn" onClick={onSubmitHandler}>
                  Submit Exam
                </button>
              ) : (
                <button onClick={() => calculateAnswer()} className="btn">
                  Next&nbsp;<i className="fa-solid fa-angles-right"></i>
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
      <footer className="text-center">
        <div className="container">
          Copyright Â© 2023.{" "}
          <a href="https://www.transfunnel.com/" target="_blank">
            TransFunnel Consulting
          </a>
        </div>
      </footer>
    </div>
  );
}

export default Question;
