import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { useAuth } from "../context/auth";
import axios from "axios";

const Login = () => {
  const [auth, setauth] = useState({});
  const [Otp, setOtp] = useState("");
  const [Valid, setValid] = useState(false);
  const navigate = useNavigate();

  const search = useLocation().search;
  const id = new URLSearchParams(search).get("id");
  // const id = "64dc96351f44ad405eddccee";

  //event handler
  const onChangeHandler = (e) => {
    e.preventDefault();
    const userOtp = e.target.value;
    if (userOtp) {
      setOtp(userOtp);
    }
  };

  //validation
  const userValidation = () => {
    if (Valid) {
      navigate("/home");
    } else {
      window.alert("please enter valid otp");
    }
  };
  useEffect(() => {
    const getUserInfo = async () => {
      try {
        const { data } = await axios.post(
          "https://assessment.inboundacademy.in/api/user/login",
          {
            id: id,
            otp: Otp,
          }
        );
        if (data) {
          // console.log(data);
          setauth({ ...auth, user: data.userInfo, token: data.token });
          localStorage.setItem("auth", JSON.stringify(data));
          setValid(true);
          // console.log(auth.user);
          navigate("/home");
        } else {
          window.alert("something went wrong");
        }
      } catch (error) {
        window.alert(error.message);
      }
    };
    getUserInfo();
  }, []);
  return (
    <div class="otp_wraper">
      <div class="otp_form">
        <div class="logo_otp text-center">
          <a target="_blank" href="#">
            <img
              width="170"
              src="images/inbound-academy-logo.webp"
              alt="Logo"
            />
          </a>
        </div>
        <div class="otp_content text-center">
          <h3>Verify Mobile</h3>
          <p>Enter the OTP (One Time Password) Sent to +91 91525852585</p>
          <input
            onChange={() => onChangeHandler}
            type="text"
            placeholder="Enter OTP here"
            required
          />

          {/* <input type="submit" value="Verify OTP" /> */}
          <button
            className="btn"
            type="submit"
            onClick={() => userValidation()}
          >
            VERIFY OTP
          </button>
        </div>
        {/* <div class="otp_resend text-center">
          <p>Check your Email for the OTP</p>
          <a href="#">Resend</a>
        </div> */}
      </div>
    </div>
  );
};

export default Login;
