import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/auth";
import axios from "axios";
import { toast } from "react-toastify";

const Home = () => {
  const [auth, setauth] = useAuth();
  const navigate = useNavigate();

  const checkUserValidation = async () => {
    try {
      const { data } = await axios.post(
        "http://localhost:4001/api/user/seven-days",
        {
          id: auth.user._id,
        }
      );
      if (data.success) {
        navigate("/question");
      } else {
        toast.error(data.msg);
      }
    } catch (error) {
      console.log(error.message);
    }
  };
  return (
    <div class="content_wraper">
      <div class="logo">
        <div class="container">
          <a target="_blank" href="https://www.inboundacademy.in/">
            <img src="images/inbound-academy-logo.webp" alt="Logo" />
          </a>
        </div>
      </div>
      <div class="body_content">
        <div class="container">
          <div class="exam_start">
            <div class="exam_start_cnt">
              <div class="exam_img">
                <img width="100%" src="images/0x0.webp" />
              </div>
              <div class="exam_guid">
                <h2>Thank you for showing your intrest</h2>
                <p>
                  Neque porro quisquam est qui dolorem ipsum quia dolor sit
                  amet, consectetur, adipisci{" "}
                </p>
                <ul>
                  <li>
                    Neque porro quisquam est qui dolorem ipsum quia dolor sit
                    amet
                  </li>
                  <li>Where does it come from?</li>
                  <li>
                    The standard Lorem Ipsum passage, used since the 1500s
                  </li>
                </ul>
              </div>
            </div>
            <div class="exam_start_cta text-center">
              {/* <a href="#">Start Exam</a> */}
              {/* <Link to={"/question"}></Link> */}
              <button onClick={checkUserValidation} className="btn">
                Start Exam
              </button>
            </div>
          </div>
        </div>
      </div>
      <footer class="text-center">
        <div class="container">
          Copyright Â© 2023.{" "}
          <a href="https://www.transfunnel.com/" target="_blank">
            TransFunnel Consulting
          </a>
        </div>
        {/* <div>{startRecording()}</div> */}
      </footer>
    </div>
  );
};

export default Home;
