import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/auth";
import axios from "axios";
import { toast } from "react-toastify";

const ShowResult = (props) => {
  const [auth, setauth] = useAuth();
  const [UserReport, setUserReport] = useState({});

  // console.log(auth.user);
  const navigate = useNavigate();
  useEffect(() => {
    const getUserScore = async () => {
      try {
        const { data } = await axios.post(
          "https://assessment.inboundacademy.in/api/user/user-score",
          {
            userId: auth.user._id,
          },
          {
            headers: {
              Authorization: `Bearer ${auth.token}`,
            },
          }
        );
        if (data) {
          console.log(data);
          setUserReport(data);
          // setScore(Score.push(data.categoryResult));
          toast.success(
            `Congratulations  ${auth.user.name}! Your test was completed successfully`
          );
          // console.log(Score);
          // localStorage.clear();
          // window.location.href = "https://www.inboundacademy.in/";
        }
      } catch (error) {
        console.log(error.message);
        // window.location.href = "https://www.inboundacademy.in/";
      }
    };
    getUserScore();
  }, []);
  return (
    <div class="content_wraper">
      <div class="logo">
        <div class="container">
          <a target="_blank" href="https://www.inboundacademy.in/">
            <img src="images/inbound-academy-logo.webp" alt="Logo" />
          </a>
        </div>
      </div>
      <div class="body_content">
        <div class="container">
          <div class="exam_start">
            <div class="exam_result_gh">
              <div class="exam_name">Inbound Academy Assessement Test</div>
              <div class="exam_score_details">
                <div class="candidate_dt">
                  <span>
                    Candidate Name:{" "}
                    <strong>{auth?.user?.name ? auth.user.name : ""}</strong>
                  </span>

                  <span>
                    Number of Questions Attempted:{" "}
                    <strong>{UserReport.total_attempted}</strong>
                  </span>
                </div>
                <div class="result_box">
                  <span>Total Score:</span>
                  <div class="reult_score">
                    {UserReport.marks_Obtained}/{UserReport.max_marks}
                  </div>
                  {UserReport.marks_Obtained < 7 ? (
                    <span className="gd_bag">Failed</span>
                  ) : (
                    <span class="gd_bad">PASSED</span>
                  )}
                </div>
              </div>
            </div>
            <div>
              <table>
                <thead>
                  <tr>
                    {/* <th>Sr No.</th> */}
                    <th>Subject</th>
                    <th>Attempted</th>
                    <th>Correct Answer</th>
                    <th>Marks</th>
                  </tr>
                </thead>
                <tbody>
                  {UserReport?.categoryResult &&
                    Object.entries(UserReport.categoryResult).map(
                      (item, index) => (
                        <tr>
                          {/* <td>{++index}</td> */}
                          <td>{item[0] ? item[0] : "-"}</td>
                          <td>{item[1]?.attempted ? item[1]?.attempted : 0}</td>
                          <td>
                            {item[1]?.correctAnswer
                              ? item[1]?.correctAnswer
                              : 0}
                          </td>
                          <td>{item[1]?.score ? item[1]?.score : 0}</td>
                        </tr>
                      )
                    )}
                </tbody>
              </table>
            </div>
            <div class="exam_start_cta text-center">
              {auth?.score >= 7 ? (
                <Link to={"payment"}>Proceed Payment</Link>
              ) : (
                ""
              )}
            </div>
          </div>
        </div>
      </div>
      <footer class="text-center">
        <div class="container">
          Copyright Â© 2023.{" "}
          <a href="https://www.transfunnel.com/" target="_blank">
            TransFunnel Consulting
          </a>
        </div>
      </footer>
    </div>
  );
};

export default ShowResult;
