import { useState, useEffect, useContext, createContext } from "react";
import axios from "axios";

const AuthContext = createContext();
const AuthProvider = ({ children }) => {
  const [auth, setAuth] = useState({
    user: {
      name: "Akash",
    },
    token: "",
    score: 12,
  });

  console.log(auth);
  //default axios
