  //       user: parseData.user,
  //       token: parseData.token,
  //       score: parseData.score,
  //     });
  //   }
  //   //eslint-disable-next-line
  // }, []);
  return (
    <AuthContext.Provider value={[auth, setAuth]}>
      {children}
    </AuthContext.Provider>
  );
};

// custom hook
const useAuth = () => useContext(AuthContext);

export { useAuth, AuthProvider };
