import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/auth";
import axios from "axios";

const Login = () => {
  const [auth, setauth] = useState({});
  cosnt[(Opt, setOtp)] = useState("");
  const navigate = useNavigate();

  //event handler
  const onChangeHandler = (e) => {
    e.preventDefault();
    const userOtp = e.target.value;
    if (userOtp) {
      setOtp(userOtp);
    }
  };
  useEffect(() => {
    const getUserInfo = async () => {
      try {
        const { data } = await axios.post(
          "http://localhost:5000/api/user/login",
          {
            phone: "9999999999",
            otp: Otp,
          }
        );
        if (data) {
          setauth({ ...auth, user: data.userInfo, token: data.token });
          localStorage.setItem("auth", JSON.stringify(data));
          navigate("/home");
        }
      } catch (error) {
        console.log(error.message);
      }
    };
    getUserInfo();
  }, []);
  return (
    <div class="otp_wraper">
      <div class="otp_form">
        <div class="logo_otp text-center">
          <a target="_blank" href="#">
            <img
              width="170"
              src="images/inbound-academy-logo.webp"
              alt="Logo"
            />
          </a>
        </div>
        <div class="otp_content text-center">
          <h3>Verify Mobile</h3>
          <p>Enter the OTP (One Time Password) Sent to +91 91525852585</p>
          <input
            onChange={() => onChangeHandler}
            type="text"
            placeholder="Enter OTP here"
            required
          />

          {/* <input type="submit" value="Verify OTP" /> */}
          <button className="btn" type="submit">
            VERIFY OTP
          </button>
        </div>
        <div class="otp_resend text-center">
          <p>Check your Email for the OTP</p>
          <a href="#">Resend</a>
        </div>
      </div>
    </div>
  );
};

export default Login;
